import { useAuth } from "../../Context/AuthrorizeContext";
import React, { useState, useEffect } from "react";
import img from '../homeimg/Jewellery_Banner_AH_1920.jpg';
import LiveGoldPriceDisplay from "./LiveGoldPriceDisplay";

export default function Home() {
    const { user } = useAuth();
    const [scrollY, setScrollY] = useState(0);
    const [containerHeight, setContainerHeight] = useState(0);

    useEffect(() => {
        const handleScroll = () => {
            setScrollY(window.scrollY);
        };

        const handleResize = () => {
            setContainerHeight(document.getElementById("home-container").clientHeight);
        };

        window.addEventListener("scroll", handleScroll);
        window.addEventListener("resize", handleResize);

        handleResize(); // Initial height calculation

        return () => {
            window.removeEventListener("scroll", handleScroll);
            window.removeEventListener("resize", handleResize);
        };
    }, []);

    return (
        <>
        <LiveGoldPriceDisplay/>
        <div id="home-container" style={{ minHeight: "100vh", display: "flex", flexDirection: "column", alignItems: "center", position: "relative" }}>
            <div style={{ backgroundColor: "#FCDEDA", width: "100%", padding: "90px" }}>
                <h2>Home Component</h2>
                {!user ? <p>User not logged in</p> : <p>Welcome { user.username }</p>}
            </div>
            <div style={{ position: "absolute", top: `${containerHeight}px`, width: "100%", display: "flex", justifyContent: "center", alignItems: "center" }}>
                <img
                    src={img} 
                    alt="Your Image"
                    style={{ maxWidth: "100%", height: "auto" }}
                />
            </div>
        </div>
        </>
    );
}
