import React from 'react';
import { useAuth } from '../../Context/AuthrorizeContext';
import { Container, Row, Col, Card, CardImg, CardBody, CardTitle, CardSubtitle, Carousel, CarouselItem, CarouselControl, CarouselIndicators, CarouselCaption } from 'reactstrap';
import { Link } from 'react-router-dom';
import backgroundImage from '../../Components/images-home/img2.jpg';

const items = [
  {
    src: 'img.jpg',
    altText: 'Image 1',
    caption: 'Slide 1'
  },
  {
    src: 'img2.jpg',
    altText: 'Image 2',
    caption: 'Slide 2'
  },
  {
    src: 'img3.jpg',
    altText: 'Image 3',
    caption: 'Slide 3'
  },
  {
    src: 'img4.jpg',
    altText: 'Image 4',
    caption: 'Slide 4'
  }
];

const Home = () => {
  const { user } = useAuth();

  return (
    <div>
      <div style={{ backgroundColor: '#ffb6c1', color: '#fff', padding: '20px 0', textAlign: 'right' ,minHeight: '20vh' }}>
        <Link style={{ color: '#fff', marginRight: '50px', fontSize: '18px' }} to="/signup">Register</Link>
        <Link style={{ color: '#fff', marginRight: '50px', fontSize: '18px' }} to="/login">Login</Link>
      </div>

      <div style={{ height: '50vh', backgroundImage: `url(${backgroundImage})`, backgroundSize: 'cover', backgroundPosition: 'center' }}>
        <h2 style={{ textAlign: 'center', paddingTop: '20vh', color: '#fff' }}>Welcome to Our Jewelry Store</h2>
      </div>

      <Container>
        <Row className="mt-4">
          <Col>
            {!user ? (
              <p></p>
            ) : (
              <p>Welcome, {user.username}</p>
            )}
          </Col>
        </Row>

        <Row className="mt-4">
          <Col>
            <Carousel>
              {items.map((item, index) => (
                <CarouselItem key={index}>
                  <img src={item.src} alt={item.altText} />
                  <CarouselCaption captionText={item.caption} />
                </CarouselItem>
              ))}
              <CarouselControl direction="prev" />
              <CarouselControl direction="next" />
            </Carousel>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default Home;
