import { useEffect, useState } from "react"
import { useDispatch, useSelector } from "react-redux"
import { startGetPaymentHistory } from "../Actions/customersAction"
import { Table, Button } from 'react-bootstrap'

export default function PaymentHistory() {
    const dispatch = useDispatch()
    const [pdfUrls, setPdfUrls] = useState({})
    const paymentHistory = useSelector((state) => state.payment.chitPayment)

    useEffect(() => {
        dispatch(startGetPaymentHistory())
    }, [dispatch])

    const [selectedPayment, setSelectedPayment] = useState(null);

    const handlePdfDownload = (paymentId) => {
        const payment = paymentHistory.find(payment => payment._id === paymentId);
        setSelectedPayment(payment);
    };

    return (
        <div style={{ paddingTop: '70px' }}>
            <Table striped bordered hover responsive>
                <thead>
                    <tr>
                        <th>Date</th>
                        <th>Time</th>
                        <th>TransactionId</th>
                        <th>GoldPrice</th>
                        <th>GoldHarvested</th>
                        <th>Payment Type</th>
                        <th>Amount</th>
                        <th>Status</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {paymentHistory.map((ele) => (
                        <tr key={ele._id}>
                            <td>{new Date(ele.paymentDate).toLocaleDateString()}</td>
                            <td>{new Date(ele.paymentDate).toLocaleTimeString()}</td>
                            <td>{ele.transactionId}</td>
                            <td>{ele.goldPrice}</td>
                            <td>{ele.goldHarvested}</td>
                            <td>{ele.paymentType}</td>
                            <td>{ele.amount}</td>
                            <td>{ele.paymentStatus}</td>
                            <td>
                                <Button variant="primary" onClick={() => handlePdfDownload(ele._id)}>Download PDF</Button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </Table>
            <div style={{ display: 'none' }}>
                {selectedPayment && (
                    <PDFViewer width="1000" height="600">
                        {generatePdfDocument(selectedPayment)}
                    </PDFViewer>
                )}
            </div>
        </div>
    )
}
