import { useAuth } from "../../Context/AuthrorizeContext";
import React, { useState, useEffect } from "react";
import img from '../homeimg/Jewellery_Banner_AH_1920.jpg';
import LiveGoldPriceDisplay from "./LiveGoldPriceDisplay";

export default function Home() {
    const { user } = useAuth();
    const [scrollY, setScrollY] = useState(0);

    useEffect(() => {
        const handleScroll = () => {
            setScrollY(window.scrollY);
        };

        window.addEventListener("scroll", handleScroll);

        return () => {
            window.removeEventListener("scroll", handleScroll);
        };
    }, []);

    return (
        <>
        <LiveGoldPriceDisplay/>
        <div style={{ minHeight: "100vh", display: "flex", flexDirection: "column", alignItems: "center", position: "relative" }}>
            <div style={{ backgroundColor: "#FCDEDA", width: "100%", padding: "90px", zIndex: "1" }}>
                <h2>Home Component</h2>
                {!user ? <p>User not logged in</p> : <p>Welcome { user.username }</p>}
            </div>
        </div>
        <img
            src={img} 
            alt="Your Image"
            style={{ position: "absolute", top: `${scrollY}px`, left: "0", width: "100%", zIndex: "0" }}
        />
        </>
    );
}
