import {useState} from 'react'
import { useDispatch,useSelector } from 'react-redux'
import {startCreateJewels} from '../Actions/Jewels'
export default function JewelForm(props){
    

    const jewels = useSelector((state)=>{
        return state.jewels
      })


    const [images, setImages] = useState([]);
    const [price, setPrice] = useState('');
    const [caption, setCaption] = useState('');
    const [jewel,setJewels] = useState({})

    const {editId} = props
    const dispatch = useDispatch()

    useEffect(() => {
        const jewel = jewels?.data.find(ele=>ele._id == editId) 
        setJewels(jewel)
        if(jewel){
            setImages(jewel.images || [])
            setPrice (jewel.price || '')
            setCaption(jewel.caption || '')
        } else {
            setImages([])
            setPrice('')
            setCaption('')
        }
    }, [jewels,editId]);


    const handleImageChange = (e) => {
        const files = Array.from(e.target.files);
        setImages([...images, ...files]);
    };



    const handleSubmit = async (e)=>{
        e.preventDefault()
        const formData = new FormData()
        images.forEach((image) => {
            formData.append('image', image)
        });
        formData.append('price', price)
        formData.append('caption', caption)


        if (jewel) {
            dispatch(startUpdateJewels(initialValues._id, formData));
        } else {
            dispatch(startCreateJewels(formData));
        }
        dispatch(resetJewelForm())
    
    }

    return(
        <div>
            <h2>Jewel Form</h2>
            {/* <form>
                <div>
                    <input type = "file"
                    />
                </div>
            </form> */}
             <form onSubmit={handleSubmit}>
                <div>
                <label htmlFor="image">Images</label>
                <input 
                type="file" 
                id="image" 
                onChange={handleImageChange} 
                accept="image/*" 
                multiple required />
                </div>
                <div>
                <label htmlFor="price">Price:</label>
                <input 
                type="number" 
                id="price" 
                value={price} 
                onChange={(e) => setPrice(e.target.value)} 
                />
                </div>
                <div>
                <label htmlFor="caption">Caption:</label>
                <input type="text" 
                id="caption" 
                value={caption} 
                onChange={(e) => setCaption(e.target.value)} />
                </div>
                <input type = 'submit'/>
            </form>
        </div>
    )
}
