import DataTable from 'react-data-table-component'
import { useDispatch, useSelector } from 'react-redux';
export default function ShopDetails(){
    const dispatch = useDispatch()
    const allShops = useSelector((state) => state.admin.allShops);

    useEffect(() => {
        dispatch(startGetAllShop())
    }, [dispatch]);

    const columns = [
        {
            name: 'Shop Name',
            selector: 'shopName',
            sortable: true,
        },
        {
            name: 'Area',
            selector: 'address.area',
            sortable: true,
        },
        {
            name: 'Pincode',
            selector: 'address.pincode',
            sortable: true,
        },
        {
            name: 'City',
            selector: 'address.city',
            sortable: true,
        },
        {
            name: 'State',
            selector: 'address.state',
            sortable: true,
        },
        {
            name: 'Mobile',
            selector: 'contact.mobile',
            sortable: true,
        },
        {
            name: 'Email',
            selector: 'contact.email',
            sortable: true,
        },
        {
            name: 'Description',
            selector: 'description',
            sortable: true,
        },
        {
            name: 'Approval Status',
            selector: 'approvalStatus',
            sortable: true,
        },
    ]

    return (
        <>
            <h2>Shop List</h2>
            <DataTable
                columns={columns}
                data={allShops}
                pagination
                highlightOnHover
                striped
                responsive
            />
        </>
    )
}
