import DataTable from 'react-data-table-component'
import {useSelector } from 'react-redux';
export default function ShopDetails(){
    const allShops = useSelector((state) => state.admin.allShops);
    
    const columns = [
        {
            name: 'Shop',
            selector : row => row.shopName
        },
        {
            name: 'Address',
            cell: row => <div style={{ whiteSpace: 'pre-wrap' }}>{`${row.address.area}, ${row.address.city}, ${row.address.state} ${row.address.pincode}`}</div>
        },
        {
            name: 'email',
            selector : row => row.contact.email
        },
        {
            name: 'mobile',
            selector : row => row.contact.mobile
        },
        {
            name: 'Description',
            selector : row => row.description
        },
        {
            name: 'Status',
            selector : row => row.approvalStatus
        }
    ]

    
    return (
        <>
            <h2>Shop List</h2>
            <DataTable
             columns={columns}
             data={allShops}
             pagination
             highlightOnHover
             striped
             responsive
            />
            
        </>
    )
}
