const initialState = {
    allShops : []
}

export const adminReducer = (state = initialState,action)=>{
    switch(action.type){

        case 'SET_ALL_SHOPS' : {
            return {...state, allShops : action.payload }
        }

        case 'SET_SHOP_STATUS': {
            const updatedShops = state.allShops.map(shop => {
                if (shop._id === action.payload.id) {
                    return {
                        ...shop,
                        approvalStatus: action.payload.newStatus
                    }
                }
                return shop
            })

            return {
                ...state,
                allShops: updatedShops
            };
        }
        default : {
            return {...state}
        }
    }
}
