import { useAuth } from "../../Context/AuthrorizeContext";
import React from "react";
import img from '../homeimg/Jewellery_Banner_AH_1920.jpg';
import LiveGoldPriceDisplay from "./LiveGoldPriceDisplay";

export default function Home() {
    const { user } = useAuth();

    return (
        <>
        <LiveGoldPriceDisplay/>
        <div style={{ minHeight: "100vh", display: "flex", flexDirection: "column", alignItems: "center", paddingTop: "90px" }}>
            <div style={{ backgroundColor: "#FCDEDA", width: "100%" }}>
                <h2 style={{ textAlign: "center" }}>Home Component</h2>
                {!user ? <p style={{ textAlign: "center" }}>User not logged in</p> : <p style={{ textAlign: "center" }}>Welcome { user.username }</p>}
            </div>
            <div style={{ display: "flex", justifyContent: "center", alignItems: "center", height: "calc(100vh - 180px)" }}>
                <img
                    src={img} 
                    alt="Your Image"
                    style={{ maxWidth: "100%", height: "auto" }}
                />
            </div>
        </div>
        </>
    );
}
