import { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { startRemoveJewels, startGetJewels } from '../Actions/Jewels';
import JewelForm from './JewelForm';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, Container, Row, Col } from 'reactstrap'; // Import Reactstrap components
import '../../index.css';

export default function JewelsTable() {
    const [modal, setModal] = useState(false);
    const [editId, setEditId] = useState('');

    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(startGetJewels());
    }, [dispatch]);

    const jewels = useSelector((state) => {
        return state.jewels;
    });

    const toggle = () => setModal(!modal);

    const handleRemove = (id) => {
        const userConfirm = window.confirm("Are you sure?");
        if (userConfirm) {
            dispatch(startRemoveJewels(id));
        }
    };

    const handleAddToWishlist = (id) => {
        // Implement your logic to add the item to the wishlist
        console.log(`Added item with ID ${id} to wishlist.`);
    };

    return (
        <div style={{ overflowY: 'auto', maxHeight: '80vh' }}>
            <Container className="mt-4" style={{ paddingTop: '60px', paddingBottom: '60px' }}>
                <Row className="justify-content" style={{ margin: '20px 20px' }}>
                    {jewels && jewels.data && jewels.data.map((ele, index) => (
                        <Col key={index} lg={4} md={6} className="mb-4">
                            <div className="card shadow-sm h-100" style={{ margin: '60px 20px 60px', height: '900px', position: 'relative' }}>
                                <div className="image-container">
                                    <img
                                        src={`http://localhost:3009/${ele.images}`}
                                        className="card-img-top img-fluid"
                                        alt="Jewelry"
                                        style={{ width: '100%' }} // Adjust the height here
                                    />
                                    <span
                                        className="wishlist-icon"
                                        onClick={() => handleAddToWishlist(ele._id)}
                                        style={{ position: 'absolute', top: '10px', right: '10px' }} // Position the heart icon
                                    >
                                        <i className="far fa-heart"></i>
                                    </span>
                                </div>
                                <div className="card-body">
                                    <p className="card-text">{ele.caption}</p>
                                    <p className="card-text">{ele.price}</p>
                                    <div className="buttons">
                                        <Button
                                            onClick={() => {
                                                setEditId(ele._id);
                                                toggle();
                                            }}
                                            color="primary"
                                        >
                                            Edit
                                        </Button>{' '}
                                        <Button
                                            onClick={() => {
                                                handleRemove(ele._id);
                                            }}
                                            color="danger"
                                        >
                                            Remove
                                        </Button>
                                    </div>
                                </div>
                            </div>
                        </Col>
                    ))}
                </Row>
            </Container>

            <Modal isOpen={modal} toggle={toggle}>
                <ModalHeader toggle={toggle}>Edit Jewel</ModalHeader>
                <ModalBody>
                    <JewelForm editId={editId} toggle={toggle} />
                </ModalBody>
                <ModalFooter>
                    <Button color="primary" onClick={toggle}>
                        Save Changes
                    </Button>{' '}
                    <Button color="secondary" onClick={toggle}>
                        Cancel
                    </Button>
                </ModalFooter>
            </Modal>
        </div>
    );
}
