import React from 'react';
import { useAuth } from '../../Context/AuthrorizeContext';
import { Container, Row, Col, Card, CardImg, CardBody, CardTitle, CardSubtitle } from 'reactstrap';
import { Link } from 'react-router-dom'; // Assuming you're using React Router for navigation

const Home = () => {
    const { user } = useAuth();

    return (
        <div>
            {/* Colored strip with links */}
            <div style={{ backgroundColor: '#00008b', color: '#fff', padding: '20px 0', textAlign: 'left' }}>
  {/* <Link style={{ color: '#fff', marginRight: '20px' }} to="/">Home</Link> */}
  <Link style={{ color: '#fff', marginRight: '20px', fontSize: '18px' }} to="/signup">Register</Link>
  <Link style={{ color: '#fff', fontSize: '18px' }} to="/login">Login</Link>
</div>

            {/* Image Section */}
            <div style={{ height: '50vh', backgroundImage: 'url(/path/to/jewelry.jpg)', backgroundSize: 'cover', backgroundPosition: 'center' }}>
                <h2 style={{ textAlign: 'center', paddingTop: '20vh', color: '#fff' }}>Welcome to Our Jewelry Store</h2>
            </div>
            {/* Featured Products */}
            <Container>
                <Row className="mt-4">
                    <Col>
                        {!user ? (
                            <p>User not logged in</p>
                        ) : (
                            <p>Welcome, {user.username}</p>
                        )}
                    </Col>
                </Row>
                <Row className="mt-4">
                    <Col>
                        <h3>Featured Products</h3>
                    </Col>
                </Row>
                <Row>
                    <Col sm="6" md="4" lg="3">
                        <Card>
                            <CardImg top width="100%" src="/path/to/image1.jpg" alt="Product 1" />
                            <CardBody>
                                <CardTitle>Product 1</CardTitle>
                                <CardSubtitle>$100</CardSubtitle>
                            </CardBody>
                        </Card>
                    </Col>
                    <Col sm="6" md="4" lg="3">
                        <Card>
                            <CardImg top width="100%" src="/path/to/image2.jpg" alt="Product 2" />
                            <CardBody>
                                <CardTitle>Product 2</CardTitle>
                                <CardSubtitle>$150</CardSubtitle>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
            </Container>
        </div>
    );
};

export default Home;
