const initialState = {
    data: []
}

const wishlistReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'SET_WISHLISTS': {
            return { ...state, items: action.payload }
        }
   
    
        case 'REMOVE_WISHLISTS': {
            return {
                ...state,
                items: state.items.filter((wishlist) => wishlist._id !== action.payload)
            }
        }
        default: {
            return { ...state }
        }
    }
}

export default wishlistReducer
